import React, {useState, useContext} from 'react';

import {MenuSettingsWrapper, MenuSettingsItem} from '../../components/Profile/styledProfileHeader'


export const MenuEdit = ({tab, setTab}) => {
    return (
        <>
            <MenuSettingsWrapper>
                <MenuSettingsItem active={tab === 1} onClick={() => setTab(1)} >Edit Profile</MenuSettingsItem>
                <MenuSettingsItem active={tab === 2} onClick={() => setTab(2)}>Profile Photo</MenuSettingsItem>
                <MenuSettingsItem active={tab === 3} onClick={() => setTab(3)}>Photogallery</MenuSettingsItem>
                <MenuSettingsItem active={tab === 4} onClick={() => setTab(4)} >Security</MenuSettingsItem>

            </MenuSettingsWrapper>
        </>
    );
};


export const MenuSettings = ({tab, setTab}) => {
    return (
        <>
            <MenuSettingsWrapper>
                <MenuSettingsItem active={tab === 1} onClick={() => setTab(1)} >Notifications</MenuSettingsItem>
                <MenuSettingsItem active={tab === 2} onClick={() => setTab(2)} >Payment methods</MenuSettingsItem>
                <MenuSettingsItem active={tab === 3} onClick={() => setTab(3)} >Payout preferences</MenuSettingsItem>
                <MenuSettingsItem active={tab === 4} onClick={() => setTab(4)} >Transaction history</MenuSettingsItem>
                <MenuSettingsItem active={tab === 5} onClick={() => setTab(5)} >Privacy</MenuSettingsItem>
                <MenuSettingsItem active={tab === 6} onClick={() => setTab(6)} >Security</MenuSettingsItem>
            </MenuSettingsWrapper>
        </>
    );
};
