import React, {useState} from 'react';
import Router from 'next/router'

import {Button} from "../../controls/Button/Button";
import {
    Title,
    CheckLabel
} from "./../../Layouts/styledLayouts";


const Based = ({startDate}) => {
    return (
        <>
            <Title mb={15} >Based on your settings, here’s what you could expect</Title>

            <CheckLabel blue label={`As for now, you’re available to be booked from ${startDate}.`}/>
            <CheckLabel blue label='Let’s imagine a client is planning an event and thinks that your offer is a perfect match.'/>
            <CheckLabel blue label='So the client sends you a message with the booking offer, says he has an event and would like you to perform there.'/>
            <CheckLabel blue label='You coordinate the details like time and location, the length of the event, as well as the number of guests and on-time payment for the services.'/>
            <CheckLabel blue label='So, well, time to perform and create wonderful memories!'/>
        </>
    );
};

export default Based;
