import React, { useState, useContext } from 'react';

import {Title, Label} from "./../../Layouts/styledLayouts";
import {Input, TextArea} from "../../controls/Input/Input";
import {Counter} from "../../controls/Counter/Counter";
import {Wrapper} from "../../StyledComponents/Wrapper";
import {Text} from "../../StyledComponents/Text";
import {MainContext} from "../../contextProviders/MainContext";

export default ({onChange}) => {
    const [count, setCount] = useState(0)
    const [arr, setArr] = useState([])
    const {me} = useContext(MainContext)

    const onChangeCount = ({name, value, required}) => {
        setCount(value)
        let clone = [...arr]

        if(clone.length < value) {
            clone.push('')
        } else {
            clone.pop()
        }
        setArr(clone)
    }

    const onChangeEmail = (key, value) => {
        let clone = [...arr]
        clone[key] = value
        setArr(clone)

        onChange({target: {name: 'listEmails', value: clone}})
    }


    return (
        <>
            <Title mb={25} >
                How many people are involved in your offer (in total)?
            </Title>

            <Wrapper mb={25}>
                <Counter
                    name=''
                    value={count}
                    onChange={onChangeCount}
                    readOnly
                />
            </Wrapper>

            {!!arr.length && <Text mb={15} fz={18}>Invite participants</Text>}

            {
                arr.map((item, key) =>
                    <Input
                        key={key}
                        type='text'
                        name=''
                        value={arr[key]}
                        onChange={(e) => onChangeEmail(key, e.target.value)}
                        mb={15}
                        placeholder="Enter email"
                    />
                )
            }


        </>
    );
};
