import React from 'react';
import styled from 'styled-components'
import { device } from '../../../lib/mediaDevice'
import {Wrapper} from "../../StyledComponents/Wrapper";
import {Title} from "../../Layouts/styledLayouts";
import {Divider} from "../../StyledComponents/Divider";

import OfferParams from "../../controls/OfferParams/OfferParams";

const Conditions = ({params, step, onChange}) => {

    return (
        <Box>
            <Title mb={25} >What conditions would you like to have?</Title>
            <Wrapper mt={30} mb={10} >
                <Divider grey full/>
                {!!params.length && <OfferParams params={params} step={step} onChange={onChange}/>}
                {/*<ButtonWithIcon plusBlue mt={20} >Add more conditions</ButtonWithIcon>*/}
            </Wrapper>


        </Box>
    );
};

const Box = styled.div`
  @media ${device.laptopDesktop} {
    ${Divider} {
      display: none;
    }
  }
`

export default Conditions;
