//     bottom: 0;
//     background: #F2F2F2;
//     height: 130px;
//     padding-left: 10%;
//
//     > button {
//       width: 340px;
//     }
//   }
// `
export const FooterDesktop = styled.div`
  @media ${device.laptopDesktop} {
    position: fixed;
    width: 100%;
    left: 0;
    bottom: 0;
    background: #f2f2f2;
    height: 100px;
    padding-left: 10%;
    padding-top: 27px;

    ${Footer} {
      width: 350px;
      margin-top: 0;
      padding-top: 0;
    }

    button {
      width: 160px;
      margin-top: 0;
    }
  }
`

export const FlexLeft = styled.div``
export const FlexRight = styled.div``
export const Flex = styled.div`
  @media ${device.laptopDesktop} {
    display: flex;

    ${FlexLeft} {
      width: 50%;
      padding-right: 20px;
    }

    ${FlexRight} {
      width: 75%;
      padding-left: 20px;
    }
  }
`
