import React, { useState, useContext } from 'react';
import Link from "next/link";
import Router from 'next/router'
import MainProvider, { MainContext, MainConsumer } from "../components/contextProviders/MainContext"

import {Wrapper} from "../components/StyledComponents/Wrapper";
import {Button} from "../components/controls/Button/Button";
import {Divider} from "../components/StyledComponents/Divider";
import {Close} from '../components/StyledComponents/Icon'
import {FlexBlock} from '../components/StyledComponents/FlexBlock'
import {Text} from "../components/StyledComponents/Text";
import {Input} from '../components/controls/Input/Input'
import validate from "../utils/validation";
import {apiLoginUser} from "../actions/api";
import {showNotification} from "../utils/notification";
import {auth, login} from "../utils/auth";
import {initAxios} from "../config/axios";

const LoginPage = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [loading, setLoading] = useState(false)

    const onChangeEmail = e => {
        let {value} = e.target
        setEmail(value)
    }
    const onChangePassword = e => {
        let {value} = e.target
        setPassword(value)
    }
    const {setMe} = useContext(MainContext)

    const handleSubmit = () => {
        setLoading(true);
        apiLoginUser({email: email.toLowerCase(), password})
            .then(async res => {
                let {token, id} = res.data;
                await login({ token, id })
                await setMe(res.data)
                // TODO check if is first time
                initAxios(() => {
                    Router.push('/')
                })

            })
            .catch(err => {
                setLoading(false);
                showNotification('error', err)
            })
    }



    let disabled = loading || !email || !password;

    return (
        <>
            <Wrapper p={25}>
                <Wrapper mb={35}>
                    <Close onClick={() => Router.back()} />
                </Wrapper>
                <Wrapper textCenter mb={20} >
                    Login with <a href={'https://talentbooking.net/api/v1/user/auth/facebook'}>Facebook</a> or <a href={'https://talentbooking.net/api/v1/user/auth/google'}>Google</a>
                </Wrapper>
                <FlexBlock mb={20} >
                    <Divider grey full/>
                    <Text fz={15} >or</Text>
                    <Divider grey full />
                </FlexBlock>
                <Input type={'text'}
                       value={email}
                       onChange={onChangeEmail}
                       placeholder="Email address"
                       mb={'5px'}
                       fz={15}
                />
                <Input type={'password'}
                       value={password}
                       placeholder="Password"
                       onChange={onChangePassword}
                       fz={15}
                />
                <Button green mt={25} onClick={handleSubmit} loading={loading} >Login</Button>
                <Divider grey full mt={25} mb={15} />
                <FlexBlock right>
                    <Text mr={10} >New to Talentbooking?</Text>
                    <Link href={'/registration'}>
                        <a><Text isLink textRight >Sign up</Text></a>
                    </Link>
                </FlexBlock>
            </Wrapper>

        </>
    );
};

export default LoginPage;
