import React, { useState, useContext, useEffect } from 'react'
import Router, { useRouter } from 'next/router'
import moment from 'moment'
import { MainContext } from '../../../components/contextProviders/MainContext'

import Discounts from '../../../components/Offer/discounts/Discounts'
import Based from '../../../components/Offer/discounts/Based'
import LocalLaws from '../../../components/Offer/discounts/LocalLaws'
import { Wrapper, WrapperHeight } from '../../../components/StyledComponents/Wrapper'
import { Button } from '../../../components/controls/Button/Button'
import { Footer } from '../../../components/Layouts/styledLayouts'
import Header from '../../../components/Header/Header'
import withOfferInfo from '../../../utils/offer'
import { apiOfferCreateDiscounts, apiEditOffer } from '../../../actions/api'
import { showNotification } from '../../../utils/notification'
import useOffer from '../../../hooks/useOffer'
import HowSetPrice from '../../../components/Offer/price/HowSetPrice'
import BasePrice from '../../../components/Offer/price/BasePrice'
import KickstartPrice from '../../../components/Offer/price/KickstartPrice'
import { ContainerDesktop, FooterDesktop } from '../../../components/StyledComponents/Grid'
import styled from 'styled-components'
import { device } from '../../../lib/mediaDevice'

const DiscountsPage = () => {
  const router = useRouter()
  const { id } = router.query
  const offer = useOffer(id)
  const { setOffer } = useContext(MainContext)
  const [step, setStep] = useState(1)
  const [loading, setLoading] = useState(false)
  const [discounts, setDiscounts] = useState({
    nD3: '',
    nD7: '',
    nD28: '',
  })

  console.log(offer)

  useEffect(() => {
    if (offer.id) {
      if (offer.discounts && offer.discounts.length) {
        let defaultDiscount = {
          nD3: offer.discounts[0].discount || '',
          nD7: offer.discounts[1].discount || '',
          nD28: offer.discounts[2].discount || '',
        }
        setDiscounts(defaultDiscount)
      }
    }
  }, [offer.id])

  const handleChangeStep = (i) => {
    if (step === 1 && i === -1) {
      Router.push('/offers/[id]/price', `/offers/${id}/price`, { shallow: true })
      return
    }
    if (step === 3 && i === 1) {
      createDiscounts()
    } else {
      setStep(step + i)
    }
  }

  const onChange = (obj) => {
    let { name, value } = obj
    setDiscounts((discounts) => ({ ...discounts, [name]: value }))
  }

  const createDiscounts = () => {
    const clone = { ...discounts }
    clone.offerId = id
    clone.discounts = [
      {
        numberDays: 3,
        discount: clone.nD3,
      },
      {
        numberDays: 7,
        discount: clone.nD7,
      },
      {
        numberDays: 28,
        discount: clone.nD28,
      },
    ]

    setLoading(true)
    apiEditOffer(id, { step: 7 })
    apiOfferCreateDiscounts(clone)
      .then((res) => {
        setLoading(false)
        if (res.err) return
        setOffer({ ...offer, discounts: res.data.discounts })
        Router.push('/offers/[id]/congratulations', `/offers/${id}/congratulations`, {
          shallow: true,
        })
      })
      .catch((e) => {
        setLoading(false)
        showNotification('error', e)
      })
  }

  const disabled = loading
  const startDate = moment(offer.startDate).format('DD MMMM')

  return (
    <>
      <Header isReg />
      <Wrapper p={17} pt={35}>
        {offer.id ? (
          <>
            <ContainerDesktop isOffer>
              <WrapperHeight>
                {step === 1 && <Discounts discounts={discounts} onChange={onChange} />}
                {step === 2 && (
                  <Based discounts={discounts} startDate={startDate} onChange={onChange} />
                )}
                {step === 3 && <LocalLaws discounts={discounts} onChange={onChange} />}
              </WrapperHeight>
            </ContainerDesktop>

            <FooterDesktop>
              <Footer mt={30}>
                {step !== 3 && <Button onClick={() => handleChangeStep(-1)}>Back</Button>}
                <Button
                  blue
                  onClick={() => handleChangeStep(1)}
                  loading={loading}
                  w100={step === 3}
                >
                  {step === 3 ? 'Agrees and continue' : 'Next'}
                </Button>
              </Footer>
            </FooterDesktop>
          </>
        ) : (
          <>Loading...</>
        )}
      </Wrapper>
    </>
  )
}

export default DiscountsPage
