import React, {useState, useContext, useEffect, useRef} from 'react';
import Router from 'next/router'
import moment from 'moment/moment'
import ImageGallery from "react-image-gallery";

import {CheckLabel, Title, WhoCard} from "../../components/Layouts/styledLayouts";
import {Wrapper} from "../../components/StyledComponents/Wrapper";
import Header from "../../components/Header/Header"
import { withAuthSync} from '../../utils/auth'
import {FlexBlock} from "../../components/StyledComponents/FlexBlock";
import {ProfileAvatar} from "../../components/Profile/styledProfileHeader";
import {modifySrc} from "../../utils/modifySrc";
import {Text} from "../../components/StyledComponents/Text";
import {ButtonMinHeight} from "../../components/controls/Button/Button";
import {Divider} from "../../components/StyledComponents/Divider";
import {HorizontalScrollItem, HorizontalScrollWrapper} from "../../components/controls/Scroll/Scroll";
import {MainContext} from "../../components/contextProviders/MainContext";
import {apiGetListUserOffers} from "../../actions/api";
import OfferCardItem from "../../components/Offer/cards/OfferCardItem";
import {Wallpaper, DotList} from "../../components/Profile/styledUser"
import {ButtonWithIcon} from "../../components/controls/Button/ButtonWithIcon";




const UserContent = ({user}) => {
    const [arrayPhotos, setArrayPhotos] = useState([])
    const [offers, setOffers] = useState([])
    const [loadingOffers, setLoadingOffers] = useState([])

    const {me, setMe} = useContext(MainContext)
    const refGallery = useRef(null)

    useEffect(() => {
        if(user.photo && user.photo.length) {
            try{
                let coverUrl = (user.coverPhoto && user.coverPhoto.photoUrl) ? user.coverPhoto.photoUrl : null
                let filteredPhoto = user.photo.filter(item => (item.photoUrl && user.photoUrl !== coverUrl))
                if(coverUrl) filteredPhoto.unshift({photoUrl: coverUrl})
                let images = filteredPhoto.map(item => {
                    if(item.photoUrl)  {
                        return {original: modifySrc(item.photoUrl, 'coverProfile')}
                    }
                })
                setArrayPhotos(images)
            } catch (e) {
                setArrayPhotos([])
            }

        }
    }, [])

    useEffect(() => {
        if(user.id) {
            setLoadingOffers(true)
            apiGetListUserOffers(user.id)
                .then(res => {
                    setOffers(res.data)
                    setLoadingOffers(false)
                })
                .catch(e => {
                    setLoadingOffers(false)
                })
        }
    }, [user.id])



    let profilePhotoSrc = modifySrc(user.profilePhotoURL, 'profile')
    let coverSrc =  modifySrc((user.coverPhoto &&  user.coverPhoto.photoUrl) ?  user.coverPhoto.photoUrl : null, 'coverProfile')



    const handleFullScreen = () => {
        refGallery.current.fullScreen()
    }

    console.log('arrayPhotos', arrayPhotos)

    const isOrganizer = user.role.includes('organiser')


    return (
        <>
            <Wallpaper src={coverSrc}>
                {!!arrayPhotos.length && <ButtonWithIcon gallery textRight onClick={handleFullScreen}>Gallery</ButtonWithIcon> }
            </Wallpaper>

            <ImageGallery
                ref={refGallery}
                items={arrayPhotos}
                showThumbnails={false}
                showPlayButton={false}
                additionalClass={'z-profile-gallery'}
            />

            <Wrapper pl={16} pr={16} pb={50}>
                <FlexBlock left mb={30} column={isOrganizer} verticalcenter={isOrganizer}>
                    <ProfileAvatar src={profilePhotoSrc} alt={'logo'} />
                    <FlexBlock column nowidth mt={30} ml={12} verticalcenter={isOrganizer} center={isOrganizer} >
                        <Text bold fz={18}>{`${user.firstName || ''} ${user.lastName || ''}`}</Text>
                        <Text fz={10}>Joined {moment(user.createdAt, "YYYYMMDD").fromNow()}</Text>
                    </FlexBlock>
                </FlexBlock>

                {isOrganizer ?
                    <>
                        {user.country && <FlexBlock mb={20} center><Text fz={16}>Lives in {user.country}</Text></FlexBlock>}
                        {user.phone && <FlexBlock mb={20} center><Text green2 fz={16}>{user.phone}</Text> </FlexBlock> }
                        {user.email && <FlexBlock mb={20} center><Text green2 fz={16}>{user.email}</Text></FlexBlock> }
                    </>
                    :
                    <>
                        {user.country &&
                        <FlexBlock left ml={25} mb={20}>
                            <DotList/>
                            <Text fz={16}>Lives in {user.country}</Text>
                        </FlexBlock>
                        }
                        {user.phone &&
                        <FlexBlock left ml={25} mb={20}>
                            <DotList/>
                            <Text green2 fz={16}>{user.phone}</Text>
                        </FlexBlock>
                        }
                        {user.email &&
                        <FlexBlock left ml={25} mb={20}>
                            <DotList/>
                            <Text green2 fz={16}>{user.email}</Text>
                        </FlexBlock>
                        }
                    </>
                }





                {/*{me.id !== user.id &&*/}
                {/*<ButtonMinHeight transparentGreen bold mt={20} onClick={() => Router.push('/inbox')}>Open messages</ButtonMinHeight>*/}
                {/*}*/}


                {/*<ButtonMinHeight  bold mt={20} onClick={() => Router.push('/profile/edit')}>Edit</ButtonMinHeight>*/}
                {/*<ButtonMinHeight  bold mt={20} onClick={() => Router.push('/profile/settings')}>Settings</ButtonMinHeight>*/}
                {/*<Text logo logostar fz={18} mt={50} ml={50}>Reviews</Text>*/}
                {/*<Text logo logocheck fz={18} mt={15} ml={50}>Verified</Text>*/}
                <Divider grey mt={40} mb={22}/>
                <Text bold fz={18}>About</Text>
                <Text fz={14} mt={15}>{user.describe}</Text>
                <Divider grey mt={28} mb={22}/>
                <Text bold fz={18} mb={30}>{`${user.firstName || ''} ${user.lastName || ''}`} provided</Text>


                {/*<CheckLabel grbl label='Government ID'/>*/}
                <CheckLabel grbl label='Email address'/>
                {/*<CheckLabel grbl label='Phone number'/>*/}

                <Divider grey mt={28} mb={22}/>

                <Text bold fz={18} mb={30}>{`${user.firstName || ''} ${user.lastName || ''}`} listings</Text>
                {loadingOffers ?
                    <>
                        Loading...
                    </>
                    :
                    <HorizontalScrollWrapper>
                        {offers.map((item, key) =>
                            <HorizontalScrollItem key={key}>
                                <OfferCardItem offer={item} />
                            </HorizontalScrollItem>
                        )}
                    </HorizontalScrollWrapper>
                }

                {/*<Text isLink fz={14} mt={28}>View all 19 listings</Text>*/}
                {/*<Divider grey mt={22} mb={22}/>*/}
                {/*<Text bold fz={18} mb={30}>20 reviews</Text>*/}
                {/*<HorizontalScrollWrapper>*/}
                {/*    <ReviewTabBlock active={true} title={"From clients"} number={5} />*/}
                {/*    <ReviewTabBlock active={false} title={"From organizers"} number={7} />*/}
                {/*    <ReviewTabBlock active={false} title={"From talents"} number={8} />*/}
                {/*</HorizontalScrollWrapper>*/}
                {/*<Divider grey mb={22}/>*/}
                {/*<FlexBlock left>*/}
                {/*    <ReviewAvatar/>*/}
                {/*    <FlexBlock column nowidth>*/}
                {/*        <Text bold fz={14} ml={12}>Samantha Cox</Text>*/}
                {/*        <Text fz={12} ml={12}>Dubai, Apr 2, 2015</Text>*/}
                {/*    </FlexBlock>*/}
                {/*</FlexBlock>*/}
                {/*<Text isLink fz={12} mt={15} mb={15}>Link to the offer</Text>*/}
                {/*<Text>For a live action we require changing rooms for artists. Stage has to be fully prepared to the show,*/}
                {/*    wich can al</Text>*/}
                {/*<Divider grey mt={20} mb={22}/>*/}
                {/*<FlexBlock left>*/}
                {/*    <ReviewAvatar/>*/}
                {/*    <FlexBlock column nowidth>*/}
                {/*        <Text bold fz={14} ml={12}>Samantha Cox</Text>*/}
                {/*        <Text fz={12} ml={12}>Dubai, Apr 2, 2015</Text>*/}
                {/*    </FlexBlock>*/}
                {/*</FlexBlock>*/}
                {/*<Text isLink fz={12} mt={15} mb={15}>Link to the offer</Text>*/}
                {/*<Text>For a live action we require changing rooms for artists. Stage has to be fully prepared to the show,*/}
                {/*    wich can al</Text>*/}
                {/*<Divider grey mt={20} mb={25}/>*/}
                {/*<Text isLink fz={16} mt={28}>Show more reviews</Text>*/}
                {/*<Divider grey mt={25} mb={25}/>*/}
                {/*<Text isLink fz={16} mt={28}>Report Alexandro Gnus</Text>*/}
                {/*<Divider grey mt={25} mb={20}/>*/}
            </Wrapper>
        </>

    );
};

export default UserContent
