import React from 'react';
import Router from 'next/router'

import {Title} from  "./../../Layouts/styledLayouts";
import {Text} from "../../StyledComponents/Text";
import {Divider} from "../../StyledComponents/Divider";
import {Wrapper} from "../../StyledComponents/Wrapper";
import {CounterLabel} from "../../controls/Counter/Counter";

const PerformanceDetails = ({pd, onChange}) => {
    const preOnChange = ({name, value}) => {
        onChange({type: name, value: value})
    }

    return (
        <>
            <Title mb={20} >Offer details</Title>
            <Text fz={18} black >
                How many times one after another/ during the day or event can you execute your offer? <br/>
                You can set the minimum and maximum numbers for one event.
            </Text>

            <Wrapper mt={30} mb={10} >
                <Divider grey full />
                <CounterLabel title='Minimum' name={'maxNumberOfActs'} value={pd.maxNumberOfActs} onChange={preOnChange} />
                <CounterLabel title="Maximum" name={'minNumberOfActs'} value={pd.minNumberOfActs} onChange={preOnChange} />
            </Wrapper>

            <Text mt={25} >
                Tip: The more times you can perform within one event, the higher can be the remuneration for one booking
            </Text>
        </>
    );
};

export default PerformanceDetails;
