import React, { useState, useEffect } from 'react';
import Link from "next/link";
import { MainContext, MainConsumer } from "../contextProviders/MainContext"

import {Wrapper} from "../StyledComponents/Wrapper";
import {Button} from "../controls/Button/Button";
import {Divider} from "../StyledComponents/Divider";
import {Close} from '../StyledComponents/Icon'
import {FlexBlock} from '../StyledComponents/FlexBlock'
import {Text, ServerError} from "../StyledComponents/Text";
import {Input} from '../controls/Input/Input'
import {CheckboxWithLabel} from '../controls/Checkbox/Checkbox'
import DatePicker from '../controls/DateTimePickers/Datepicker'
import validate from "../../utils/validation";
import {hasError} from "../../utils/utils";
import {showNotification} from "../../utils/notification";
import {Router} from "../../routes";

import {apiCreateUser} from '../../actions/api'

const RegEmail = ({setView}) => {
    const defaultUserData = {
        email: '',
        firstName: '',
        lastName: '',
        password: '',
        birthday: null,
        offerId: localStorage.getItem('ioid')
    }
    const [user, setUser] = useState(defaultUserData)
    const [errors, setErorrs] = useState({})
    const [checked, setCheck] = useState(false)
    const [loading, setLoading] = useState(false)
    const [serverError, setServerError] = useState(false)
    const [data, setData] = useState('initial')

    console.log('user', user)

    const onChange = e => {
        let {name, value, required} = e.target
        if(name === 'email' || name === 'password') value = value.trim()
        setUser(user => ({...user, [name]: value}))
        let error = validate(name, value, required)
        setErorrs(errors => ({...errors, [name]: error}))
    }
    const onChangeBirthday = birthday => {
        setUser(user => ({...user, birthday: birthday}))
    }
    const handleSubmit = () => {
        setLoading(true);
        let clone = {...user, email: user.email.toLowerCase()}
        apiCreateUser(clone)
            .then(res => {
                showNotification('info', `Check your mail ${user.email} to activate account`)
                localStorage.removeItem('ioid')
                Router.pushRoute('/login')
            })
            .catch(err => {
                setLoading(false);
                showNotification('error', err)
            })
    }


    let disabled = hasError(errors) || !user.email || !user.firstName || !user.lastName || !user.password;

    return (
        <>
            <Wrapper mb={35}>
                <Close onClick={() => setView(false)} />
            </Wrapper>
            <Wrapper textCenter mb={20} >
                Sign up with&nbsp;
                <Link href={'/registration'} ><a>Facebook</a></Link>
                &nbsp;or&nbsp;
                <Link href={'/registration'}><a>Google</a></Link>
            </Wrapper>
            <FlexBlock mb={20} >
                <Divider grey full/>
                <Text fz={15} >or</Text>
                <Divider grey full />
            </FlexBlock>
            <Input value={user.email}
                   name={'email'}
                   onChange={onChange}
                   error={errors.email}
                   placeholder="Email address"
                   required
                   mb={'5px'}
                   fz={15}
            />
            <Input value={user.firstName}
                   name={'firstName'}
                   onChange={onChange}
                   error={errors.firstName}
                   placeholder="First name"
                   required
                   mb={'5px'}
                   fz={15}

            />
            <Input value={user.lastName}
                   name='lastName'
                   onChange={onChange}
                   error={errors.lastName}
                   placeholder="Last name"
                   required
                   fz={15}
                   mb={'5px'}
            />
            <Input value={user.password}
                   name='password'
                   type='password'
                   onChange={onChange}
                   error={errors.password}
                   required
                   placeholder="Create a password"
                   fz={15}
            />

            <Text mt={15} fz={15} bold >Birthday</Text>
            <Text>To sign up, you must be 18 or older. Other people won’t see your birthday.</Text>

            <Wrapper mt={20} mb={20} width100 >
                <DatePicker
                    selected={user.birthday}
                    name='birthday'
                    maxDate={new Date()}
                    onChange={onChangeBirthday}
                    withPortal
                    showYearDropdown
                    dateFormat="dd/MM/yyyy"
                />
            </Wrapper>

            <Text mb={20} >We’ll send you marketing promotions, special offers, inspiration, and policy updates via email.</Text>
            <Button green disabled={disabled} loading={loading} onClick={handleSubmit} >Create account</Button>
            <ServerError >{serverError}</ServerError>

            <Wrapper mt={22}>
                <CheckboxWithLabel
                    checked={checked}
                    onChange={() => setCheck(!checked)}
                    label={'I don’t want to receive marketing messages from Talentbooking. I can also opt out of receiving these at any time in my account settings or via the link in the message.'}
                />
            </Wrapper>

            <Divider grey full mt={20} mb={15} />

            <FlexBlock right >
                <Text pr={15} >Already signed up?</Text>
                <Link href={'/login'}>
                    <Text isLink >Log in</Text>
                </Link>
            </FlexBlock>
        </>
    );
};

export default RegEmail;
