import React, {useState} from 'react';
import Router from 'next/router'

import {Button} from "../../controls/Button/Button";
import {Title, WrapButton, TipWrapper, Label, WrapRadioBlock, TipWrapper2, Label2} from "./../../Layouts/styledLayouts";
import {Text} from "../../StyledComponents/Text";
import {Divider} from "../../StyledComponents/Divider";
import {Wrapper} from "../../StyledComponents/Wrapper";
import {CounterLabel} from "../../controls/Counter/Counter";
import Select from "../../controls/Select/Select";
import {Clr1, Clr2} from "../../StyledComponents/Icon";
import {RadioBtn} from "../../controls/Radio/Radio";
import {Input} from "../../controls/Input/Input";
import {modifyForReactSealect} from "../../../utils/utils";

const Discounts = ({discounts, onChange}) => {
    return (
        <>
            <Title mb={15} >Discounts</Title>
            <Label>
                The longer is the booking, the more remuneration you will get for it.
                Encourage clients to make long-term deals by setting special discounts.
            </Label>

            <Label2 mt={20} >Discount for 3+ days </Label2>
            <Input type={'number'}
                   value={discounts.nD3}
                   onChange={e => onChange({name: 'nD3', value: e.target.value})}
                   mb={10}
                   placeholder="0% off"
            />
            <Text blue mb={40} fz={18} >Tip. 10 %</Text>

            <Label2 >Discount for 7+ days </Label2>
            <Input type={'number'}
                   value={discounts.nD7}
                   onChange={e => onChange({name: 'nD7', value: e.target.value})}
                   mb={10}
                   placeholder="0% off"
            />
            <Text blue mb={40} fz={18} >Tip. 20 %</Text>

            <Label2 >Discount for 28+ days</Label2>
            <Input type={'number'}
                   value={discounts.nD28}
                   onChange={e => onChange({name: 'nD28', value: e.target.value})}
                   mb={10}
                   placeholder="0% off"
            />
            <Text blue mb={40} fz={18} >Tip. 30 %</Text>
        </>
    );
};

export default Discounts;
