import React, { useState, useContext } from 'react';
import {ButtonWithIcon} from "../controls/Button/ButtonWithIcon";
import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'

import config from '../../config'


const FacebookLoginBtn = () => {
    const responseFacebook = (response) => {
        window.location.href='https://talentbooking.net/api/v1/user/auth/facebook'
    }

    return (
        <>
            <ButtonWithIcon facebook  mb={10} onClick={responseFacebook}>Register with Facebook</ButtonWithIcon>

            {/*<FacebookLogin*/}
            {/*    appId={config.facebookAppId}*/}
            {/*    autoLoad={false}*/}
            {/*    callback={responseFacebook}*/}
            {/*    render={renderProps => (*/}
            {/*        <ButtonWithIcon facebook  mb={10} onClick={renderProps.onClick}>Register with Facebook</ButtonWithIcon>*/}
            {/*        )}*/}
            {/*/>*/}
        </>
    );
};

export default FacebookLoginBtn;
