import React, { useState, useContext } from 'react';

import {Title, Label} from "./../../Layouts/styledLayouts";
import {Input, TextArea} from "../../controls/Input/Input";

export default ({offer, onChange}) => {

    return (
        <>
            <Title mb={25} >Now it’s time to create your offer!</Title>
            <Label >Name your offer</Label>
            <Input
                name='name'
                value={offer.name}
                onChange={onChange}
                mb={30}
                placeholder="Ex.: Singing opera"
            />

            <Label >Add a short summary of the offer</Label>
            <TextArea
                name='summary'
                value={offer.summary}
                onChange={onChange}
                height={95}
                maxLength={300}
                placeholder="Ex.: Singing rock opera covers of the hits "

            />
        </>
    );
};
