import React, {useState} from 'react';
import Router from 'next/router'

import {Button} from "../../controls/Button/Button";
import {Title, WrapButton, TipWrapper, Label, WrapRadioBlock, TipWrapper2} from "./../../Layouts/styledLayouts";
import {Text} from "../../StyledComponents/Text";
import {Divider} from "../../StyledComponents/Divider";
// import {Wrapper} from "../../StyledComponents/Wrapper";
import {CounterLabel} from "../../controls/Counter/Counter";
import Select from "../../controls/Select/Select";
import {Clr1, Clr2, WrapperIcon} from "../../StyledComponents/Icon";
import {RadioBtn} from "../../controls/Radio/Radio";
import styled from "styled-components";
import {device} from "../../../lib/mediaDevice";

const HowSetPrice = ({price, onChange}) => {

    return (
        <Wrapper>
            <Title mb={40} >How do you want to set your price?</Title>
            <Divider grey full mt={10} mb={10}></Divider>



            <Wrap>
                <Box>
                    <Label>Price is fixed</Label>
                    <WrapRadioBlock  >
                        <Clr2 />
                        <Text>
                            Set a base price. Programmerbooking can give you price tips which you may either accept or ignore.
                        </Text>
                        <RadioBtn blue checked={price.typePrice == 'fixed'} onClick={() => onChange({type: 'typePrice', value: 'fixed'})}  />
                    </WrapRadioBlock>

                    <Divider grey full mt={10} mb={10}></Divider>
                </Box>
                <Box>
                    <Label disabled>Price adapts to demand</Label>
                    <WrapRadioBlock disabled >
                        <Clr1 />
                        <Text>
                            Our Smart Pricing will automatically adjust your price to match the demand,
                            the price will be set within the range that you set.
                        </Text>
                        {/*<RadioBtn blue checked={price.typePrice == 'adapted'} onClick={() => onChange({type: 'typePrice', value: 'adapted'})} />*/}
                        <RadioBtn blue checked={price.typePrice == 'adapted'} />
                    </WrapRadioBlock>
                </Box>
            </Wrap>



            {/*<TipWrapper2 mt={60} mb={10} >*/}
            {/*    <Text fz={12} grey4a>*/}
            {/*        The right price can change as the number of searches for listings like yours goes up and down.*/}
            {/*        Whichever price option you choose, you’ll get tips to help you set prices for your offer.*/}
            {/*    </Text>*/}
            {/*</TipWrapper2>*/}
        </Wrapper>
    );
};

export default HowSetPrice;

const Wrapper = styled.div`
  @media ${device.laptopDesktop} {
    ${Divider} {
      display: none;
    }
  }
`
const Box = styled.div``
const Wrap = styled.div`
  @media ${device.laptopDesktop} {
    display: flex;
    justify-content: space-between;

    ${Box} {
      width: 45%;
    }
   
  }
`
