import React, { useState } from 'react'
import Router from 'next/router'
import styled, { css } from 'styled-components'
import { Button } from '../../controls/Button/Button'
import { Title, CheckLabel } from './../../Layouts/styledLayouts'
import { Text } from '../../StyledComponents/Text'
import { Divider } from '../../StyledComponents/Divider'
import { device } from '../../../lib/mediaDevice'

const LocalLaws = ({ discounts, onChange }) => {
  return (
    <Wrapper>
      <Title mb={15} textCenter>
        Your local laws and taxes
      </Title>
      <Divider hideDsk green half mt={20} mb={20}></Divider>
      <Text fz={18} textCenter>
        Make sure you familiarize yourself with your local laws, as well as E booking
        Nondiscrimination Policy.
      </Text>
      <Divider green half mt={20} mb={20}></Divider>
      <Text fz={18} textCenter>
        Please educate yourself about the laws in your jurisdiction before listing your act.
      </Text>
      <Divider green half mt={20} mb={20}></Divider>
      <Text fz={18} textCenter>
        By accepting our Terms of Service and listing your offer, you certify that you will follow
        applicable laws and regulations.
      </Text>
    </Wrapper>
  )
}

export default LocalLaws

const Wrapper = styled.div`
  @media ${device.laptopDesktop} {
    width: 50%;
    margin: 0 auto;

    ${Divider} {
      margin-top: 30px;
      margin-bottom: 30px;

      ${(p =>
        p.hideDsk &&
        css`
          display: none;
        `)}
    }
  }
`
