import React, { useState, useContext, useEffect, memo } from 'react'
import Link from 'next/link'
import axios from 'axios'
import styled, { css } from 'styled-components'
import { device } from '../lib/mediaDevice'
import { MainContext, MainConsumer } from '../components/contextProviders/MainContext'
import SearchBlock from '../components/Offer/search/SearchBlock'
import {
  MainSubTitle,
  MainTitle,
  SubTitle,
  SubTitle16,
  Text,
} from '../components/StyledComponents/Text'
import { Divider } from '../components/StyledComponents/Divider'
import ModalCookies from '../components/Modal/ModalCookies'
import Header from '../components/Header/Header'
import { Button } from '../components/controls/Button/Button'
import Router from 'next/router'
import { HomepageHeaderWrapper } from '../components/Header/styledHeader'
import ListOffersHomepage from '../components/Offer/list/ListOffersHomepage'
import { withReatom } from '../lib/withReatom'
import { useDetectDevice } from '../lib/useDetectDevice'
import Card1 from '../static/img/card-1.png'
import Card2 from '../static/img/card-2.png'
import Card3 from '../static/img/card-3.png'

const MainPage = () => {
  const currentDevice = useDetectDevice()
  const [isOpenedModalCookie, toogleOpenModal] = useState(false)
  const { me, checkedMe } = useContext(MainContext)

  if (!me.role) me.role = []
  let isProgrammer = me.role.includes('programmer') || me.role.includes('organiser')

  let d = new Date()
  d.setDate(d.getDate() - 1)
  let from = new Date(d).getTime()

  return (
    <>
      <Header isHomepage />
      <HomepageHeaderWrapper className="homepage-header-wrap">
        {currentDevice.isLaptopDesktop && <SearchBlock />}
        <MainTitle>
          Book programmers. <br />
          Find clients. <br />
          All here - on Programmerbooking. <br />
        </MainTitle>
        {currentDevice.isMobileTablet && (
          <>
            <SubTitle white>Look for programmers, rent equipment</SubTitle>
            <Button
              white
              bold
              onClick={() => {
                Router.push('/search')
              }}
            >
              Start searching
            </Button>
            <SubTitle white>Programmer or Event agency, to find clients easier:</SubTitle>
            {isProgrammer ? (
              <Button
                transparentBlack
                bold
                onClick={() => {
                  Router.push('/offers/create')
                }}
              >
                Create an offer
              </Button>
            ) : (
              <Button
                transparentBlack
                bold
                onClick={() => {
                  Router.push('/registration/introduce?s=2')
                }}
              >
                Become a programmer
              </Button>
            )}
          </>
        )}
      </HomepageHeaderWrapper>

      <HomeWrapper>
        {currentDevice.isMobileTablet && <SearchBlock />}

        <Holiday>
          <MainSubTitle mt={40} mb={20}>
            Holidays with Programmerbooking
          </MainSubTitle>
          <Divider grey full />
          <div className="flex-2-column">
            <div className="col-50">
              <SubTitle16 mb={10} mt={30}>
                24-hour support service
              </SubTitle16>
              <Text mb={40}>
                Day and night we are with you. Chat with our support team from anywhere in the world
                and at any time of the day.
              </Text>
            </div>
            <div className="col-50">
              <SubTitle16 mb={10} mt={30}>
                World standards
              </SubTitle16>
              <Text mb={40}>
                A unified standard for booking programmers and creating offers, applicable all over the
                world, fitting any tastes.
              </Text>
            </div>
          </div>
        </Holiday>

        <Divider grey full mb={30} />

        <WrapperDesktop>
          <Flex>
            <Link href={`/offers/categories?last=${20}`}>
              <Card>
                <img src={Card1} alt="" />
                <Info>
                  <CardInfoTitle>Recently added</CardInfoTitle>
                  <CardInfoDescr>Подборка самых крутых из самых крутых</CardInfoDescr>
                </Info>
              </Card>
            </Link>
            <Link href={`/offers/categories?search=new_year`}>
              <Card>
                <img src={Card2} alt="" />
                <Info>
                  <CardInfoTitle>New Year</CardInfoTitle>
                  <CardInfoDescr>Подборка самых крутых из самых крутых</CardInfoDescr>
                </Info>
              </Card>
            </Link>
            <Link href={`/offers/categories?type=kids`}>
              <Card>
                <img src={Card3} alt="" />
                <Info>
                  <CardInfoTitle>Kids</CardInfoTitle>
                  <CardInfoDescr>Подборка самых крутых из самых крутых</CardInfoDescr>
                </Info>
              </Card>
            </Link>
          </Flex>

          <ListOffersHomepage />
        </WrapperDesktop>
      </HomeWrapper>

      <ModalCookies
        isOpen={isOpenedModalCookie}
        afterOpenModal={() => {}}
        closeModal={() => toogleOpenModal(!isOpenedModalCookie)}
      />
    </>
  )
}

MainPage.getInitialProps = async ({ req }) => {
  console.log('index page')
  return {}
}
export default withReatom(MainPage)

const Holiday = styled.div`
  @media ${device.laptopDesktop} {
    background: #322449;
    padding: 40px 10%;

    ${MainSubTitle} {
      margin-top: 0;
      font-size: 28px;
      color: #ffffff;
    }
    ${Divider} {
      background-color: #ffffff;
      display: none;
    }
    ${SubTitle16} {
      color: #ffffff;
      font-weight: bold;
      margin-top: 10px;
      // fonnt-size: 18px;
    }
    ${Text} {
      color: #ffffff;
      font-size: 12px;
      margin-bottom: 0;
    }

    .flex-2-column {
      width: 50%;
      margin-left: 35%;
    }
  }
`
const HomeWrapper = styled.div`
  padding: 20px 15px 20px 15px;

  @media ${device.laptopDesktop} {
    padding: 0;
  }
`
const WrapperDesktop = styled.div`
  @media ${device.laptopDesktop} {
    width: 80%;
    margin: 0 auto;
  }
`
const Flex = styled.div`
  display: flex;
  justify-content: space-between;

  margin-bottom: 50px;

  @media ${device.mobile} {
    flex-direction: column;
  }

  @media ${device.laptopDesktop} {
  }
`
const Card = styled.div`
  width: 100%;
  cursor: pointer;
  border-radius: 10px;
  box-shadow: 0px 4px 14px rgba(0, 0, 0, 0.1);
  
  @media ${device.mobile} {
    margin-bottom: 20px;
  }

  @media ${device.laptopDesktop} {
    width: 32%;
  }

  img {
    width: 100%;
  }
`
const Info = styled.div`
  padding: 5%;
`
const CardInfoTitle = styled.div`
  //font-weight: bold;
  font-family: 'CircularProBold';
  font-size: 17px;
  line-height: 16px;
  color: rgba(0, 0, 0, 0.8);
  margin-bottom: 5px;
`
const CardInfoDescr = styled.div`
  font-size: 13px;
  line-height: 16px;
  color: rgba(0, 0, 0, 0.5);
`
