import React, {useState} from 'react';

import {Title} from "../../Layouts/styledLayouts";
import {Divider} from "../../StyledComponents/Divider";
import OfferParams from "../../controls/OfferParams/OfferParams";
import styled from "styled-components";
import {device} from "../../../lib/mediaDevice";


const DefineArtistRules = ({params, step, onChange}) => {

    return (
        <Box>
            <Title mb={15} >Let’s define your personal needs (to be provided) and abilities</Title>
            <Divider grey full/>
            {!!params.length && <OfferParams params={params} step={step} onChange={onChange}/>}
        </Box>
    );
};

const Box = styled.div`
  @media ${device.laptopDesktop} {
    ${Divider} {
      display: none;
    }
  }
`

export default DefineArtistRules;
