import React from 'react'
import Router from 'next/router'

import { Button, ButtonMin } from '../../controls/Button/Button'
import {
  Title,
  WrapButton,
  TipWrapper,
  Label,
  CheckLabel,
  TipWrapper2,
} from './../../Layouts/styledLayouts'
import { Text } from '../../StyledComponents/Text'
import { Divider } from '../../StyledComponents/Divider'
import styled from 'styled-components'
import { device } from '../../../lib/mediaDevice'
import { CheckGreen } from '../../StyledComponents/Icon'

const Review = ({ info }) => {
  return (
    <Box>
      <Title mb={10}>Now let’s review all requirements</Title>
      <Label mb={15}>Booking requirements set by a Client:</Label>
      <CheckLabel label="Profile photo (of you)" />
      <CheckLabel label="Confirmed email" />
      {/*<CheckLabel label='Confirmed phone number'/>*/}
      {/*<CheckLabel label='Payment information'/>*/}
      {/*<ButtonMin transparentBlue mt={20} ml={50} >Review</ButtonMin>*/}
      <Divider grey full mt={25} mb={15} />
      <Label mb={15}>Programmer’s needs for the offer</Label>

      {info.regulations.map((item, key) => {
        if (item.value === 'no') return null
        return <CheckLabel key={key} label={item.title} />
      })}

      {/*<ButtonMin transparentBlue mt={20} ml={50} >Edit</ButtonMin>*/}
      <Divider grey full mt={25} />
      <TipWrapper2 mt={15} mb={10}>
        <Text fz={12}>
          Client will only be able to book instantly with you if they meet all these requirements
          and agree to your needs.
        </Text>
      </TipWrapper2>
    </Box>
  )
}

export default Review

const Box = styled.div`
  @media ${device.laptopDesktop} {
    ${Divider} {
      margin-top: 25px;
      margin-bottom: 25px;
    }
  }
`
