import React, { useState, useContext } from 'react'

import Header from '../../components/Header/Header'
import { MenuSettings } from '../../components/Profile/MenuSettings'
import Security from '../../components/Profile/Security'
import { SettingsWrapper } from '../../components/Profile/styledProfileHeader'
import { ContainerDesktop } from '../../components/StyledComponents/Grid'
import { ButtonMinHeight } from '../../components/controls/Button/Button'
import { Divider } from '../../components/StyledComponents/Divider'
import { MainContext } from '../../components/contextProviders/MainContext'
import { withAuthSync } from '../../utils/auth'
import Link from 'next/link'
import styled from 'styled-components'
import { device } from '../../lib/mediaDevice'

const Settings = () => {
  const { me, setMe } = useContext(MainContext)
  const [tab, setTab] = useState(6)
  const [loading, setLoading] = useState(false)

  let user = Object.assign({}, me)

  return (
    <>
      <Header isProfile />
      <ContainerDesktop>
        <Flex>
          <FlexLeft>
            <MenuSettings tab={tab} setTab={setTab} />
          </FlexLeft>
          <FlexRight>
            <SettingsWrapper>
              <TopBtnWrapper>
                <Link href={'/profile'}>
                  <ButtonMinHeight transparentBlue black mt={20}>
                    View profile
                  </ButtonMinHeight>
                </Link>
              </TopBtnWrapper>
              <Divider grey mt={28} mb={22} />
              <BoxCenter>{tab === 6 && <Security user={user} loading={loading} />}</BoxCenter>
            </SettingsWrapper>
          </FlexRight>
        </Flex>
      </ContainerDesktop>
    </>
  )
}

export default withAuthSync(Settings)

const FlexLeft = styled.div``
const FlexRight = styled.div``

const Flex = styled.div`
  @media ${device.laptopDesktop} {
    display: flex;

    ${FlexLeft} {
      width: 20%;
    }

    ${FlexRight} {
      width: 80%;
    }
  }
`
const TopBtnWrapper = styled.div`
  text-align: right;
  @media ${device.laptopDesktop} {
    ${ButtonMinHeight} {
      width: 200px;
    }
  }
`
const BoxCenter = styled.div`
  @media ${device.laptopDesktop} {
    width: 400px;
    margin: 0 auto;
  }
`
