import React, {useState} from 'react';
import Router from 'next/router'

import {Button} from "../../controls/Button/Button";
import {
    Title,
    WrapButton,
    TipWrapper,
    Label,
    WrapRadioBlock,
    TipWrapper2,
    Label2,
    WrapRadioBlock2, Footer
} from "./../../Layouts/styledLayouts";
import {Text} from "../../StyledComponents/Text";
import {Divider} from "../../StyledComponents/Divider";
import {Wrapper} from "../../StyledComponents/Wrapper";
import {CounterLabel} from "../../controls/Counter/Counter";
import Select from "../../controls/Select/Select";
import {Clr1, Clr2} from "../../StyledComponents/Icon";
import {RadioBtn} from "../../controls/Radio/Radio";
import {Input} from "../../controls/Input/Input";

const KickstartPrice = ({price, onChange}) => {
    const [value, setValue] = useState("");
    const [name, setName] = useState("");
    let onChangeSelect = () => {

    }

    const options = [
        { value: 'chocolate', label: 'Chocolate' },
        { value: 'strawberry', label: 'Strawberry' },
        { value: 'vanilla', label: 'Vanilla' },
    ];

    return (
        <>
            <Title mb={40} >Kickstart your new offer with a welcome price</Title>
            <Divider grey full mb={20}></Divider>
            <Label>Offer 15% off to your first client</Label>
            <WrapRadioBlock2 twoColumn>
                <Text>
                    Welcome first clients with a special offer. After your first booking,
                    future clients will pay the full price. Offer expires after one month.
                </Text>
                <RadioBtn blue checked={price.kickstart == '15'} onClick={() => onChange({type: 'kickstart', value: '15'})} />
            </WrapRadioBlock2>
            <Divider grey full mt={20} mb={20}></Divider>
            <Label>No, I don’t want to offer a welcome pricing </Label>
            <WrapRadioBlock2 twoColumn>
                <Text>
                    Your listing will appear as normal, and client will pay the full price you set.
                    Once you finish publishing your listing, you will no longer be able to add a special welcome offer.
                </Text>
                <RadioBtn blue checked={price.kickstart == null} onClick={() => onChange({type: 'kickstart', value: null})} />
            </WrapRadioBlock2>

        </>
    );
};

export default KickstartPrice;
