import React, { useState, useContext, useEffect, memo } from 'react';
import Link from "next/link";
import axios from 'axios'
import { MainContext, MainConsumer } from "../components/contextProviders/MainContext"
import SearchBlock from '../components/Offer/search/SearchBlock'
import {MainSubTitle, MainTitle, SubTitle, SubTitle16, Text} from "../components/StyledComponents/Text"
import {Divider} from "../components/StyledComponents/Divider"
import ModalCookies from '../components/Modal/ModalCookies'
import Header from "../components/Header/Header";
import {Button} from "../components/controls/Button/Button";
import Router from "next/router";
import {HomepageHeaderWrapper} from "../components/Header/styledHeader";
import ListPopularOffers from "../components/Offer/list/ListPopularOffers"
import {withReatom} from "../lib/withReatom";




const MainPage = () => {
    const [isOpenedModalCookie, toogleOpenModal] = useState(false);
    const {me, checkedMe} = useContext(MainContext)

    if(!me.role) me.role = []
    let isTalent = me.role.includes('talent') || me.role.includes('organiser')


    return (
        <>
            <Header isHomepage />
            <HomepageHeaderWrapper className='homepage-header-wrap'>
                <MainTitle>
                    Book artists. <br/>
                    Find clients. <br/>
                    All here - on Talentbooking. <br/>
                </MainTitle>
                <SubTitle white >Look for talents, rent equipment</SubTitle>
                <Button white bold onClick={() => {Router.push('/search')}}>Start searching</Button>

                <>
                    <SubTitle white >Talent or Event agency, to find clients easier:</SubTitle>
                    {isTalent ?
                        <Button transparentBlack bold onClick={() => {Router.push('/offers/create')}} >Create an offer</Button>
                        :
                        <Button transparentBlack bold onClick={() => {Router.push('/registration/introduce')}} >Become a talent</Button>
                    }
                </>
            </HomepageHeaderWrapper>

            <div className="homepage-wrapper">
                <SearchBlock />

                <MainSubTitle mt={40} mb={20}>Holidays with Talentbooking</MainSubTitle>
                <Divider grey full />
                <div className="flex-2-column">
                    <div className="col-50">
                        <SubTitle16 mb={10} mt={30}>24-hour support service</SubTitle16>
                        <Text mb={40}>
                            Day and night we are with you. Chat with our support team from anywhere in the world and at any time of the day.
                        </Text>
                    </div>
                    <div className="col-50">
                        <SubTitle16 mb={10} mt={30} >World standards</SubTitle16>
                        <Text mb={40}>
                            Day and night we are with you. Chat with our support team from anywhere.
                        </Text>
                    </div>
                </div>
                <Divider grey full mb={30} />


                <ListPopularOffers />


            </div>

            <ModalCookies
                isOpen={isOpenedModalCookie}
                afterOpenModal={() => {}}
                closeModal={() => toogleOpenModal(!isOpenedModalCookie)}
            />
        </>
    );
};

MainPage.getInitialProps = async ({ req }) => {
    console.log('index page')
    return { }
}


export default withReatom(MainPage)
