import React, {useState, useContext} from 'react';
import Link from "next/link";
import Router from 'next/router'
import {MainContext, MainConsumer} from "../contextProviders/MainContext";
import {SubTitle} from '../StyledComponents/Text'
import {HeaderWrapper, HeaderWrapperReg, HeaderWrapperProfile, HeaderWrapperOffer, HeaderLogo, PreviewBtn} from './styledHeader'
import {Text} from "../../components/StyledComponents/Text";
import {
    Logo,
    LogoGreen,
    LogoPurple,
    Bars,
    GreenArrowDown,
    PurpleArrowDown,
    PlusPurple,
    Preferences,
    ArrowLeft
} from '../StyledComponents/Icon'
import {logout} from "../../utils/auth";
import {Input} from "../../components/controls/Input/Input";
import Sidebar from './Sidebar'
import {modifySrc} from "../../utils/modifySrc";
import styled from "styled-components";

const Header = ({isReg, isProfile, isOffer, isPreview, previewOfferCb, isHomepage, hiddeSave}) => {
    const {me, checkedMe, unreadMessages} = useContext(MainContext)
    const [openedSidebar, setOpen] = useState(false)

    const toggleSidebar = () => {
        if(!openedSidebar) {
            document.getElementsByTagName("body")[0].className = "no-scroll";
        } else {
            document.getElementsByTagName("body")[0].classList.remove("no-scroll");
        }
        setOpen(!openedSidebar)
    }

    if(process.browser && !openedSidebar) {
        document.getElementsByTagName("body")[0].classList.remove("no-scroll");
    }

    // console.log(isReg, isProfile, isOffer, isPreview)

    return (
        <>
            {isReg ?
                <HeaderWrapperReg>
                    { hiddeSave ?
                        <>{me.firstName} {me.lastName}</>
                        :
                        <div onClick={() => Router.push('/profile/offers', `/profile/offers`, {shallow: true})}>Save end Exit</div>
                    }
                </HeaderWrapperReg>
                :
                isProfile ?
                    <HeaderWrapperProfile>
                        <div onClick={toggleSidebar}>
                            <LogoGreen/>
                             <GreenArrowDown  />
                        </div>
                        {previewOfferCb ?
                            <PreviewBtn onClick={() => previewOfferCb()}>Preview</PreviewBtn>
                            :
                            <Input placeholder={"Search"} ml={10} height40/>
                        }
                    </HeaderWrapperProfile>
                    :
                    isPreview ?
                        <HeaderWrapperOffer>
                            <div>
                                <LogoPurple/>
                                <PurpleArrowDown/>
                            </div>
                            <div>
                                <Text blue>Preview</Text>
                            </div>
                        </HeaderWrapperOffer>
                    :
                    <HeaderWrapper isHomepage={!!isHomepage}>
                        <div>
                            <Logo/>
                            {me.id  && <Bars onClick={toggleSidebar} />}
                            {!!unreadMessages && <UnreadMessages/>}
                        </div>
                        {checkedMe &&
                            <>
                                {!me.id ?
                                    <Link href="/login" as={'/login'}>
                                        <SubTitle white>Login</SubTitle>
                                    </Link>
                                    :
                                    <HeaderLogo
                                        src={modifySrc(me.profilePhotoURL, 'profile')}
                                        onClick={() => Router.push('/profile')}
                                    />
                                    // <SubTitle white onClick={logout}>Logout</SubTitle>
                                }
                            </>
                        }
                    </HeaderWrapper>
            }

            {openedSidebar && <Sidebar isHomepage={!!isHomepage} toggleSidebar={toggleSidebar} logout={logout} />}
        </>

    );
};

export default Header;


const UnreadMessages = styled.span`
  display: inline-block;
    width: 8px;
    height: 8px;
    border-radius: 4px;
    background: #00C874;
    position: relative;
    top: -7px;
    right: 5px;
`;
