import React, { useState, useContext } from 'react';

import {Title, Label, TipWrapper2, Footer, EventItemWrapper, EventItem} from "./../../Layouts/styledLayouts";
import {addOrRemove} from './../../../utils/utils'
import {Input} from "../../controls/Input/Input";
import {apiCreateEventType} from "../../../actions/api";

export default ({offer, onChange, listEventTypes}) => {
    const toggleItem = (item) => {
        let arr = addOrRemove(offer.type, item);
        onChange( {target: {name: 'type', value: arr}})
    }

    return (
        <>
            <Title mb={25} >My offer is perfect for</Title>
            <EventItemWrapper>
                {listEventTypes.map((item, key) =>
                    <EventItem key={key} active={offer.type.includes(item)} onClick={() => toggleItem(item)} >{item.type}</EventItem>
                )}
            </EventItemWrapper>



        </>
    );
};
