import React, { useState, useContext } from 'react';

import {Title, Label, TipWrapper2, Footer} from "./../../Layouts/styledLayouts";
import {Input, TextArea} from "../../controls/Input/Input";
import {Text} from "../../StyledComponents/Text";
import {Button} from "../../controls/Button/Button";
import {Flex, FlexLeft, FlexRight} from '../../StyledComponents/Grid'
import styled from "styled-components";
import {device} from "../../../lib/mediaDevice";

export default ({offer, onChange}) => {

    return (
        <Box>
            <Title mb={25} >Present your offer in every detail</Title>
            <Flex>
                <FlexLeft>
                    <Label >About offer</Label>
                    <TextArea name='description'
                              value={offer.description}
                              onChange={onChange}
                              height={95}
                              maxLength={500}
                              placeholder="Give a general overview of the offer: what is it like and what it includes?"
                    />

                    <Label mt={25} >What will be done</Label>
                    <TextArea name='descriptionWillDone'
                              value={offer.descriptionWillDone}
                              onChange={onChange}
                              height={95}
                              maxLength={500}
                              placeholder="Describe your offer: give details about the schedule, program, costumes, accessories and the act itself. Be specific."
                    />
                    <Label mt={25}  >What will be provided</Label>
                    <TextArea name='descriptionWillProvide'
                              value={offer.descriptionWillProvide}
                              onChange={onChange}
                              height={95}
                              maxLength={500}
                              placeholder="Here you can dwell on the process of the performance, on equipment and interactions with the audience. What kind of output will it be?"
                    />
                </FlexLeft>
                <FlexRight>
                    <Label mt={25}  >Venue requirements</Label>
                    <TextArea name='descriptionRequirements'
                              value={offer.descriptionRequirements}
                              onChange={onChange}
                              height={95}
                              maxLength={500}
                              placeholder="Are there any preferences for the type of venue so you felt more comfortable performing the offer? Share them here, if so."
                    />
                    <Label mt={25}  >Note</Label>
                    <TextArea name='descriptionNote'
                              value={offer.descriptionNote}
                              onChange={onChange}
                              height={95}
                              maxLength={500}
                              placeholder="If there is anything you would like to notify potential clients about in advance - go for it right here."
                    />

                    <TipWrapper2 mt={40} mb={10} >
                        <Text fz={12} grey4a >
                            Be as detailed as possible, show potential clients you are a real professional with a high level of preparation.
                            Demonstrate possible ways of customization of your services to fit different preferences.
                        </Text>
                    </TipWrapper2>
                </FlexRight>
            </Flex>
        </Box>
    );
};

const Box = styled.div`
  @media ${device.laptopDesktop} {
    ${Label} {
      margin-top: 0;
    }
    textarea {
      margin-bottom: 25px;
    }
    ${TipWrapper2} {
      margin-top: 73px;
    } 
  }
`
