import React, { useState, useContext, useEffect } from 'react';
import dynamic from 'next/dynamic';
import moment from 'moment'
import Link from "next/link";
import Router, { useRouter } from 'next/router'
import {useAction, useAtom} from "@reatom/react";
import {modifyToBr} from '../../../utils/utils'
import {withReatom} from "../../../lib/withReatom";

import Header from "../../../components/Header/Header";
import {GalleryOffer} from "../../../components/Gallery/GalleryOffer";
import {Video} from "../../../components/Gallery/Video";
import {
    apiGetOfferById,
    apiReservOffer,
    apiCreateChatOffer,
    apiGetListUserOffers,
    apiGetUserById,
    apiEditOfferPhoto,
    apiOfferLike
} from "../../../actions/api";
import {Text, LabelText} from "../../../components/StyledComponents/Text";
import {FlexBlock} from "../../../components/StyledComponents/FlexBlock";
import {AvatarOffer} from "../../../components/Profile/styledProfileOffer";
import {Divider} from "../../../components/StyledComponents/Divider";
import {Button, ButtonMinHeight} from "../../../components/controls/Button/Button";
import {Wrapper} from "../../../components/StyledComponents/Wrapper";
import {showNotification} from "../../../utils/notification";
import {MainContext} from "../../../components/contextProviders/MainContext";
import useUserInfo from '../../../hooks/useUserInfo'
import {modifySrc} from "../../../utils/modifySrc";
import {Column, Container, Row} from "../../../components/StyledComponents/Grid";
import {ListLR, ListItem, ListParticipants} from "../../../components/controls/Lists/ListData"
import imgArrDown from "../../../static/img/arrow-down.svg"
import {HorizontalScrollItem, HorizontalScrollWrapper} from "../../../components/controls/Scroll/Scroll";
import OfferCardItem from "../../../components/Offer/cards/OfferCardItem";
import DetailsBook from "../../../components/Offer/detailsBook"
import {ModalContact} from "../../../components/Modal/ModalContact"


const Calendar = dynamic(import ('../../../components/controls/Calendar/Calendar'),{ssr:false});
const CalendarAirbnb = dynamic(import ('../../../components/controls/Calendar/CalendarAirbnb'),{ssr:false});

const OfferPage = () => {
    const router = useRouter()
    const {me} = useContext(MainContext)
    const { id } = router.query
    const [offer, setOffer] = useState({
        categoryOptions: []
    })
    const [offersOrganizer, setOffersOrganizer] = useState([])
    const [participants, setParticipants] = useState([])
    const [bookingDates, setBookingDates] = useState({startDate: null, endDate: null})
    const [loading, setLoading] = useState(false)
    const [loadingOffers, setLoadingOffers] = useState(false)
    const [isLoadingChat, setIsLoadingChat] = useState(false)
    const [isSendingMessage, setIsSendingMessage] = useState(false)
    const [loadingReserv, setLoadingReserv] = useState(false)
    const [selectedDays, setSelected] = useState([])
    const [showMore, setShowMore] = useState(false)
    const [detailsView, setDetailsView] = useState(false)
    const [showModalContact, setShowModalContact] = useState(false)

    // const offerById = useAtom(offersAtom, offers => {
    //     return offers.find(item => item.id === id)
    // } , [id])



    useEffect(() => {
        let getOfferInfo = async () => {
            try {
                const res = await apiGetOfferById(id)
                preInit(res.data)
            } catch (e) {
                showNotification('error', 'Error while loading offer')
            }
        }

        let preInit = (offer) => {
            let calendar = offer.calendar || [];
            let selectedArr = calendar.map(item => item.date)
            setSelected(selectedArr)
            setOffer(offer)
            getOffersOrganizer(offer.userId)
            if(offer.offerParticipants && offer.offerParticipants.length) {
                getOffersOrganizerParticipants(offer.offerParticipants)
            }
        }
        if(id) {
            getOfferInfo()
        }
        window.scrollTo(0, 0)
    }, [id]);

    useEffect(() => {
        window.scrollTo(0, 0)
    }, [detailsView])

    const getOffersOrganizer = async userId => {
        try {
            setLoadingOffers(true)
            const res = await apiGetListUserOffers(userId)
            setOffersOrganizer(res.data)
            setLoadingOffers(false)
        } catch (e) {
            setLoadingOffers(false)
        }
    }
    const getOffersOrganizerParticipants = arr => {
        let participants =  arr.map(item => item.userId)
        setParticipants(participants)
        console.log(arr)

        // try {
        //     const getUserInfo = async id => {
        //         const res = await apiGetUserById(id)
        //         console.log('////', res)
        //         return res
        //     }
        //     Promise.all(arr.map(item => getUserInfo(item.id)))
        //         .then(res => {
        //             debugger
        //             setParticipants(res.data)
        //         })
        // } catch (e) {
        //     setLoadingOffers(false)
        // }
    }
    const onChangeDates = (obj) => {
        let copy = Object.assign({}, obj);
        if(!copy.endDate || copy.endDate == 'Invalid Date') copy.endDate = null
        if(!copy.startDate || copy.startDate == 'Invalid Date') copy.startDate = null
        setBookingDates(copy)
    }
    const onChangeBookingData = (e) => {
        let {name, value} = e.target
        if(name === 'locationDetails' || name === 'additionalTerms' ) {
            value = value.slice(0, 300)
        }

        console.log(value, value.length)

        setBookingDates({...bookingDates, [name]: value})
    }

    const handleBook = () => {
        setLoadingReserv(true)
        debugger
        apiReservOffer(id, bookingDates)
            .then(res => {
                setLoadingReserv(false)
                showNotification('info', 'Success.')
                // todo: save dates to Calendar, maybe change Order
                setOffer({...offer, calendar: [...offer.calendar, ...res.data]})
                onChangeDates({})

                if(res.data && res.data[0]) {
                    Router.push('/inbox/[id]/booking', `/inbox/${res.data[0].bookingId}/booking`, {shallow: true})
                }

            })
            .catch(e => {
                showNotification('error', e)
                setLoadingReserv(false)
            })
    }


    const handleContactTalent = () => {
        // setIsLoadingChat(true)
        if(offer.preChatId) {
            Router.push('/inbox/[id]/offer', `/inbox/${offer.preChatId}/offer`, {shallow: true})
        } else {
            setShowModalContact(true)
        }
    }

    const handleSendMessage = msg => {
        setIsSendingMessage(false)
        apiCreateChatOffer(offer.id, msg)
            .then(res => {
                setIsLoadingChat(false)
                if(res.data && res.data.preChatId) {
                    setShowModalContact(false)
                    setIsSendingMessage(false)
                    Router.push('/inbox/[id]/offer', `/inbox/${res.data.preChatId.id}/offer`, {shallow: true})
                }
            })
            .catch(e => {
                setIsSendingMessage(false)
                showNotification('error', e)
                setIsLoadingChat(false)
            })
    }
    const handleToggleLike = () => {
        apiOfferLike(offer.id)
            .then(res => {
                setOffer({...offer, myLike: !offer.myLike})
            })
            .catch(e => {

            })
    }


    let disabledBtn = !bookingDates.startDate || !bookingDates.endDate || loadingReserv

    const owner = useUserInfo(offer.userId);

    let params1 = offer.categoryOptions.filter(item => (item.templateId && item.templateId.step === 1))
    let params2 = offer.categoryOptions.filter(item => (item.templateId && item.templateId.step === 2))
    let params3 = offer.categoryOptions.filter(item => (item.templateId && item.templateId.step === 3))
    let params4 = offer.categoryOptions.filter(item => (item.templateId && item.templateId.step === 4 && item.value === 'true'))

    console.log('offer', offer)
    console.log('owner', owner)
    console.log('params4', params4)

    const iOwner = me.id === offer.userId

    if(detailsView) {
        return <DetailsBook
            offer={offer}
            bookingDates={bookingDates}
            setDetailsView={setDetailsView}
            onChange={onChangeBookingData}
            handleBook={handleBook}
            loadingReserv={loadingReserv}
        />
    }


    return (
        <>
            <Header isProfile/>
            {offer.id ?
                <>
                    <GalleryOffer offer={offer} handleToggleLike={handleToggleLike} />


                    <Wrapper pl={18} pr={18} pb={80}>

                        <Text grey2 fz={16} mt={18}>
                            {(offer.subCategory && offer.subCategory.category) ?
                                offer.subCategory.category
                                :
                                offer.category.category
                            }
                        </Text>
                        <Text fz={23}>{offer.name}</Text>
                        <Link href={iOwner ? '/profile' : `/users/${offer.userId}`}>
                            <FlexBlock left verticalcenter nowidth mt={12} >
                                {owner.id &&
                                <>
                                    <AvatarOffer src={modifySrc(owner.profilePhotoURL, 'person')}/>
                                    <div>
                                        <Text ml={15} fz={16} bold>{owner.firstName} {owner.lastName}</Text>
                                        <Text ml={15} fz={14}>{owner.country}</Text>
                                        <Text ml={15}
                                              fz={10}>Joined {moment(owner.createdAt, "YYYYMMDD").fromNow()}</Text>
                                    </div>
                                </>
                                }

                            </FlexBlock>
                        </Link>


                        <Container mt={30}>
                            <Row>
                                {params1.map((item) => {
                                    if(!item.value) return null
                                    return (
                                        <Column key={item.id} xs={6} mb={15} >
                                            <Text bold >{item.value}</Text>
                                            <Text grey4a >{item.templateId.title}</Text>
                                        </Column>
                                    )
                                })}
                            </Row>
                        </Container>
                        <Divider grey mt={20} mb={20}/>

                        {offer.videoUrl && !!offer.videoUrl.length &&
                            <>
                                <HorizontalScrollWrapper>
                                    {offer.videoUrl.map((item, key) =>
                                        <HorizontalScrollItem key={key}>
                                            <Video src={item} />
                                        </HorizontalScrollItem>
                                    )}
                                </HorizontalScrollWrapper>
                                <Divider grey mt={20} mb={20}/>
                            </>
                        }



                        <Text grey4a dangerouslySetInnerHTML={modifyToBr(offer.summary)} />

                        {showMore  ?
                            <>
                                {offer.description &&
                                <>
                                    <LabelText>About Performance</LabelText>
                                    <Text grey4a mb={25} dangerouslySetInnerHTML={modifyToBr(offer.description)} />
                                </>
                                }

                                {offer.descriptionWillDone &&
                                <>
                                    <>
                                        <LabelText>What will be done</LabelText>
                                        <Text grey4a mb={25} dangerouslySetInnerHTML={modifyToBr(offer.descriptionWillDone)} />
                                    </>
                                </>
                                }
                                {offer.descriptionWillProvide &&
                                <>
                                    <>
                                        <LabelText>What will be provide</LabelText>
                                        <Text grey4a mb={25} dangerouslySetInnerHTML={modifyToBr(offer.descriptionWillProvide)} />
                                    </>
                                </>
                                }
                                {offer.descriptionRequirements &&
                                <>
                                    <>
                                        <LabelText>Venue Requirements</LabelText>
                                        <Text grey4a mb={25} dangerouslySetInnerHTML={modifyToBr(offer.descriptionRequirements)} />
                                    </>
                                </>
                                }
                                {offer.descriptionNote &&
                                <>
                                    <>
                                        <LabelText>Note</LabelText>
                                        <Text grey4a mb={25} dangerouslySetInnerHTML={modifyToBr(offer.descriptionNote)} />
                                    </>
                                </>
                                }
                                <Text isLink fz={14} mt={12} onClick={() => setShowMore(false)}>
                                    Hide details &nbsp;
                                    <img style={{transform: 'rotate(180deg)'}} src={imgArrDown} alt=""/>
                                </Text>
                            </>
                            :
                            <Text isLink fz={14} mt={12} onClick={() => setShowMore(true)}>
                                More details about an offer &nbsp;
                                <img src={imgArrDown} alt=""/>
                            </Text>

                        }

                        {!iOwner &&
                            <ButtonMinHeight transparentGreen h36 mt={24} disabled={isLoadingChat} onClick={handleContactTalent}>Contact</ButtonMinHeight>
                        }


                        <Divider grey mt={24} mb={22}/>

                        <Text bold fz={18} mt={12} mb={20}>Special conditions</Text>
                        <Container mt={20}>
                            <Row>
                                {params4.map((item, key) =>
                                    <Column key={item.id} xs={6} mb={15} >
                                        <ListItem title={item.templateId.title} type={'blackGreen'} fz={14} />
                                    </Column>
                                )}
                            </Row>
                        </Container>
                        {/*<Text isLink fz={14} mt={15}>Show all: 14</Text>*/}


                        <Divider grey mt={20} mb={20}/>
                        <Text bold fz={18} mt={12} mb={20}>Availability</Text>

                        <div style={{marginTop: '20px'}}>
                            {me.id === offer.userId ?
                            <Calendar
                                selectedDays={selectedDays}
                                enabledAll={true}
                                noEditable={true}
                                endDate={offer.endDate}
                            />
                            :
                            <CalendarAirbnb
                                userId={me.id}
                                calendar={offer.calendar}
                                endDateOffer={offer.endDate}
                                onChangeDates={onChangeDates}
                                bookingDates={bookingDates}
                            />
                            }
                        </div>

                        {/*<Divider grey full />*/}

                        <Text bold fz={18} mt={50} mb={20}>Conditions </Text>
                        {params2.map((item, key) =>
                            <ListLR key={item.id} title={item.templateId.title} value={item.value} />
                        )}

                        <Text bold fz={18} mt={30} mb={15}>Rules</Text>
                        {params3.map((item, key) =>
                            <ListItem key={item.id} title={item.templateId.title} type={'black'} />
                        )}

                        <Divider grey mt={20} mb={25}/>


                        {!!participants.length &&
                            <>
                                <Text bold fz={18} mt={20} mb={20}>Who’s participating</Text>
                                <div className="list-participants">
                                    {participants.map((item, key) =>
                                        <ListParticipants key={key} user={item} />
                                    )}
                                </div>
                                <Divider grey mt={20} mb={20}/>
                            </>
                        }

                        <Text fz={18} mt={12}>
                            <b className='text-bold'>Talant</b> {`${owner.firstName} ${owner.lastName}`}
                        </Text>
                        <Text mt={1}>{owner.country} - Joined {moment(owner.createdAt, "YYYYMMDD").fromNow()}</Text>
                        {/*<Text mt={15} mb={10}>Languages: </Text>*/}

                        {!iOwner &&
                            <ButtonMinHeight transparentGreen h36 mt={24} disabled={isLoadingChat} onClick={handleContactTalent}>Contact talent</ButtonMinHeight>
                        }

                        <Divider grey mt={20} mb={20}/>
                        <Text mt={15}>
                            <span className="text-bold">Always communicate through Talentbooking · </span>
                            To protect your payment, never transfer money or communicate outside of the
                            Talentbooking website.
                        </Text>
                        <Text isLink>Learn more</Text>
                        <Divider grey mt={20} mb={22}/>
                        <Text bold fz={18} mt={12} mb={15}>Geography offers</Text>
                        <Text mb={50}>{offer.country}</Text>
                        <Divider grey mt={20} mb={22}/>

                        {/*<Text fz={16} mb={1}>Cancellation policy</Text>*/}
                        {/*<Text fz={14}>Strict · Free cancellation for 48 hours</Text>*/}
                        {/*<Divider grey mt={20} mb={25}/>*/}

                        {(me.id !== offer.userId) &&
                            <>
                                <Text isLink fz={16} mt={28}>Report this offer</Text>
                                <Divider grey mt={25} mb={25}/>
                            </>
                        }


                        <Text bold fz={18} mb={30}>Organizer offers</Text>
                        {loadingOffers ?
                            <>
                                Loading...
                            </>
                            :
                            <HorizontalScrollWrapper>
                                {offersOrganizer.map((item, key) =>
                                    <HorizontalScrollItem key={key}>
                                        <OfferCardItem offer={item} />
                                    </HorizontalScrollItem>
                                )}
                            </HorizontalScrollWrapper>
                        }


                    </Wrapper>



                        <div className="footer-offer">
                            {(me.id === offer.userId) ?
                                <>
                                    <Button blue onClick={() => Router.push(`/offers/${offer.id}/edit`)} >Edit offer</Button>
                                </>
                                :
                                <>
                                    {offer.status === 'open' && <Button blue disabled={disabledBtn} onClick={() => setDetailsView(true)}>Book dates</Button>}
                                </>
                            }
                        </div>
                    }

                </>
                :
                <>Loading...</>
            }

            <ModalContact
                isOpen={showModalContact}
                handleCloseModal={() => setShowModalContact(false)}
                handleSendMessage={handleSendMessage}
                isLoading={isSendingMessage}
                user={owner.firstName}
            />
        </>
    );
};

export default withReatom(OfferPage)
